package Batch;import java.sql.Connection;import java.sql.PreparedStatement;import java.sql.SQLException;import java.util.Scanner;public class BatchPreparedStMain {    public static void main(String[] args) throws SQLException {        Connection cn = null;        int choose=0;        try {            cn=GetConnection.getConn();            cn.setAutoCommit(false);            PreparedStatement ps= cn.prepareStatement("Insert into emp value(?,?,?,?)");            do{                Scanner sc=new Scanner(System.in);                System.out.println("Insert employee ID");                int id=sc.nextInt();                System.out.println("Insert employee Name");                String name=sc.next();                System.out.println("Insert employee Age");                int age=sc.nextInt();                System.out.println("Insert employee Salary");                int salary=sc.nextInt();                ps.setInt(1,id);                ps.setString(2,name);                ps.setInt(3,age);                ps.setInt(4,salary);                ps.addBatch();                System.out.println("ADD more so press 1");                choose= sc.nextInt();            }while (choose==1);                int updatecount[]= ps.executeBatch();                cn.commit();                System.out.println("batch update for"+updatecount.length+" statement");        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {              cn.rollback();            e.printStackTrace();        }    }}