package Batch;import java.sql.Connection;import java.sql.SQLException;import java.sql.Statement;public class BatchStatementMain {    public static void main(String[] args) {        Connection cn=null;           try {             cn=GetConnection.getConn();            cn.setAutoCommit(false);            Statement stmt=cn.createStatement();            stmt.addBatch("INsert into emp value (101,'sarvesh',23,20000)");            stmt.addBatch("INsert into emp value (102,'anuj',23,2400)");            stmt.addBatch("INsert into emp value (103,'aditya',33,21000)");            stmt.addBatch("INsert into emp value (104,'shree',24,20300)");       int updatecount[]=stmt.executeBatch();       cn.commit();            System.out.println("batch update for"+updatecount.length+" statement");        } catch (ClassNotFoundException e) {           e.printStackTrace();        } catch (SQLException e) {               try {                   cn.rollback();               } catch (SQLException ex) {                 e.printStackTrace();               }               e.printStackTrace();        }    }}