package CallableStatement;import Employee_menuDriven.GetConnection;import java.sql.CallableStatement;import java.sql.Connection;import java.sql.SQLException;import java.sql.Types;import java.util.Scanner;public class CallADDMain {    public static void main(String[] args) {        try {            Connection cn= GetConnection.getConn();            CallableStatement cb= cn.prepareCall("{call addnum(?,?,?)}");            Scanner sc=new Scanner(System.in);            System.out.println("enter  num1");            int num1=sc.nextInt();            System.out.println("enter  num2");            int num2=sc.nextInt();            cb.setInt(1,num1);            cb.setInt(2,num2);            cb.registerOutParameter(3, Types.INTEGER);            cb.execute();            int ans=cb.getInt(3);            System.out.println("addtion= "+ans);        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {           e.printStackTrace();        }    }}