package CallableStatement;import Employee_menuDriven.GetConnection;import java.sql.CallableStatement;import java.sql.Connection;import java.sql.SQLException;import java.sql.Types;import java.util.Scanner;public class CallSquMain {    public static void main(String[] args) {        try {            Connection cn= GetConnection.getConn();            CallableStatement cb=cn.prepareCall("{call squxd(?,?)}");            Scanner sc=new Scanner(System.in);            System.out.println("enter  number");            int num=sc.nextInt();            cb.setInt(1,num);            cb.registerOutParameter(2, Types.INTEGER);            cb.execute();            int ans=cb.getInt(2);            System.out.println(num+" Square= "+ans);        } catch (ClassNotFoundException e) {                e.printStackTrace();        }        catch (SQLException e) {             e.printStackTrace();        }    }}