package CallableStatement;import Batch.GetConnection;import java.sql.CallableStatement;import java.sql.Connection;import java.sql.SQLException;import java.sql.Types;import java.util.Scanner;public class CallableXD {    public static void main(String[] args) {        try {            Connection cn= GetConnection.getConn();            System.out.println("choose-\n1.Square\n2.cube\n3.even and odd find");            Scanner sc=new Scanner(System.in);            int choose=sc.nextInt();            switch (choose){                case 1:                    CallableStatement cb=cn.prepareCall("{call squxd(?,?)}");                    System.out.println("enter  number");                    int num=sc.nextInt();                    cb.setInt(1,num);                    cb.registerOutParameter(2, Types.INTEGER);                    cb.execute();                    int ans=cb.getInt(2);                    System.out.println(num+" Square= "+ans);                    break;                case 2:                    CallableStatement cst=cn.prepareCall("{call cubexd(?,?)}");                    System.out.println("enter  number");                    int num1=sc.nextInt();                    cst.setInt(1,num1);                    cst.registerOutParameter(2, Types.INTEGER);                    cst.execute();                    int an=cst.getInt(2);                    System.out.println(num1+" Cube= "+an);                    break;                case 3:                    CallableStatement cstp=cn.prepareCall("{call evenodd(?,?)}");                    System.out.println("enter  number");                    int num3=sc.nextInt();                    cstp.setInt(1,num3);                    cstp.registerOutParameter(2, Types.INTEGER);                    cstp.execute();                    int a=cstp.getInt(2);                    if(a==1){                        System.out.println(num3+" is a ODD number" );                    }else{                        System.out.println(num3+" Is a Even number ");                    }                    break;            }        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {                      e.printStackTrace();        }    }}