package Day1;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;import java.sql.Statement;public class Update {    public static void main(String[] args) {        Connection conn = null;        try {            Class.forName("com.mysql.cj.jdbc.Driver");            System.out.println("Driver Loaded");            conn = DriverManager.getConnection("jdbc:mysql://127.0.0.1:3306/mydb", "root", "9890099723");            System.out.println("Connected");            Statement stmt = conn.createStatement();            int i = stmt.executeUpdate("insert into emp values(6,'Manas',23,239475)");            if(i>0) {                System.out.println(i+" Record Inserted");            }            else                System.out.println("No Changes");        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }        finally {            try {                conn.close();            } catch (SQLException e) {                e.printStackTrace();            }        }    }}