package Day1;import java.sql.Connection;import java.sql.DriverManager;import java.sql.ResultSet;import java.sql.SQLException;import java.sql.Statement;public class Update {    public static void main(String[] args) {        Connection conn = null;        try {            Class.forName("oracle.jdbc.driver.OracleDriver");            System.out.println("Driver Loaded");            conn = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:orcl","c##hr","hr");            System.out.println("Connected");            Statement stmt = conn.createStatement();            int i = stmt.executeUpdate("insert into emp values(105,'Manas',239475)");            if(i>0) {                System.out.println(i+" Record Inserted");            }            else                System.out.println("No Changes");        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }        finally {            try {                conn.close();            } catch (SQLException e) {                e.printStackTrace();            }        }    }}