package Employee_menuDriven;import java.sql.Connection;import java.sql.SQLException;import java.sql.Statement;import java.util.Collection;import java.util.Scanner;public class InsertRecord {    public static void insertRecord()throws ClassNotFoundException, SQLException {        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        Scanner sc=new Scanner(System.in);        System.out.println("Enter Insert record ID-");        int id=sc.nextInt();        System.out.println("Enter Insert record Name-");        String name=sc.next();        System.out.println("Enter Insert record age-");        int age=sc.nextInt();        System.out.println("Enter Insert record salary-");        int salary=sc.nextInt();        int xd= st.executeUpdate("INSERT into emp values("+id+",'"+name+"',"+age+","+salary+")" );        if(xd<0){            System.out.println("Record not Insert...");        }else {            System.out.println(xd+" Record Inserted");        }    }}