package Employee_menuDriven;import java.sql.Connection;import java.sql.ResultSet;import java.sql.SQLException;import java.sql.Statement;public class SelectRecord {    public static void selectOrderByname()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        ResultSet rs=st.executeQuery("select * from emp order by name ");        while(rs.next()){            System.out.println("ID- "+rs.getInt(1)+                    " Name- "+rs.getString(2)+                    " Age- "+rs.getInt(3)+                    " Salary- "+rs.getInt(4)            );        }    }    public static void selectOrderById()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        ResultSet rs=st.executeQuery("select * from emp order by id ");        while(rs.next()){            System.out.println("ID- "+rs.getInt(1)+                    " Name- "+rs.getString(2)+                    " Age- "+rs.getInt(3)+                    " Salary- "+rs.getInt(4)            );        }    }    public static void selectOrderBySalary()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        ResultSet rs=st.executeQuery("select * from emp order by salary ");        while(rs.next()){            System.out.println("ID- "+rs.getInt(1)+                    " Name- "+rs.getString(2)+                    " Age- "+rs.getInt(3)+                    " Salary- "+rs.getInt(4)            );        }    }    }