package Employee_menuDriven;import java.sql.*;public class SelectRecord {    public static void selectOrderByname()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        PreparedStatement ps = cn.prepareStatement("select * from emp");        ResultSet rs =ps.executeQuery();        while(rs.next()) {            System.out.println(rs.getInt(1)+"\t"+rs.getString(2)+"\t"+rs.getInt(3));        }        }    public static void selectOrderById()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        ResultSet rs=st.executeQuery("select * from emp order by id ");        while(rs.next()){            System.out.println("ID- "+rs.getInt(1)+                    " Name- "+rs.getString(2)+                    " Age- "+rs.getInt(3)+                    " Salary- "+rs.getInt(4)            );        }    }    public static void selectOrderBySalary()throws ClassNotFoundException, SQLException{        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        ResultSet rs=st.executeQuery("select * from emp order by salary ");        while(rs.next()){            System.out.println("ID- "+rs.getInt(1)+                    " Name- "+rs.getString(2)+                    " Age- "+rs.getInt(3)+                    " Salary- "+rs.getInt(4)            );        }    }    }