package Employee_menuDriven;import java.sql.Connection;import java.sql.SQLException;import java.sql.Statement;import java.util.Scanner;public class UpdateRecord {    public static void updateRecord()throws ClassNotFoundException, SQLException {        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        Scanner sc=new Scanner(System.in);        System.out.println("Enter Update record ID-");        int id=sc.nextInt();        System.out.println("Enter Update record Salary-");        int salary=sc.nextInt();        int xd= st.executeUpdate("update  emp Set salary="+salary+" where id="+id );        if(xd>0){            System.out.println(xd+" Record Update");        }else {            System.out.println("Record not Update...");        }    }    }