package Employee_menuDriven;import java.sql.Connection;import java.sql.SQLException;import java.sql.Statement;public class UpdateRecord {    public static void updateRecord()throws ClassNotFoundException, SQLException {        Connection cn=GetConnection.getConn();        Statement st= cn.createStatement();        int xd= st.executeUpdate("update  emp Set salary=2000 where id=1" );        if(xd>0){            System.out.println(xd+" Record Update");        }else {            System.out.println("Record not Update...");        }    }    }